mapper.select:
SELECT dc.DAILY_SEQ, dc.USER_ID, u.USER_NAME, p.POSITIONS_NAME, r.RANKS_NAME, de.DEPT_NAME, dc.DAILY_INTIME, dc.DAILY_OUTTIME, dc.DAILY_STATUS, dc.DAILY_MODIFY, dc.DAILY_REGDATE FROM USERINFO u INNER JOIN DAILYCHECK dc ON u.USER_ID = dc.USER_ID INNER JOIN POSITIONS p ON u.POSITIONS_SEQ = p.POSITIONS_SEQ INNER JOIN RANKS r ON u.RANKS_SEQ = r.RANKS_SEQ INNER JOIN DEPT de ON u.DEPT_SEQ = de.DEPT_SEQ WHERE 1=1
AND dc.DAILY_REGDATE BETWEEN TO_DATE(#{first_dailyregdate}, 'YYYY-MM-DD') AND TO_DATE(#{last_dailyregdate}, 'YYYY-MM-DD')
AND p.POSITIONS_NAME = #{positions_name}
AND r.RANKS_NAME = #{ranks_name}
AND de.DEPT_NAME = #{dept_name}
AND dc.USER_ID LIKE '%' || #{user_id} || '%'
AND u.USER_NAME LIKE '%' || #{user_name} || '%'
ORDER BY dc.DAILY_REGDATE DESC

mapper.update:
UPDATE DAILYCHECK
SET 
DAILY_MODIFY = #{daily_modify} , DAILY_REASONMODIFY = #{daily_reasonmodify}
WHERE DAILY_SEQ = #{daily_seq}
