mapper.select:
SELECT PBO_SEQ, USER_ID, PBO_TITLE, PBO_CONTENT, PBO_REGDATE, PBO_REF, PBO_STEP, PBO_DEPTH
FROM PROJECT_BOARD
WHERE PBO_SEQ = #{id}

mapper.update:
UPDATE PROJECT_BOARD
SET PBO_CONTENT = #{content}
WHERE PBO_SEQ = #{id}

UPDATE PROJECT_BOARD
SET PBO_DELFLAG = 'Y'
WHERE PBO_SEQ = #{id}
