mapper.update:
UPDATE RESERVATION
	SET RE_TITLE=#{re_title}, RE_CONTENT=#{re_content}, RE_START = TO_DATE(#{re_start}, 'YYYY-MM-DD HH24:MI'), 
		RE_END = TO_DATE(#{re_start}, 'YYYY-MM-DD HH24:MI')+ INTERVAL '1' HOUR, ME_ROOM=#{me_room}
	WHERE RE_SEQ=#{re_seq}

UPDATE RESERVATION
	SET RE_DELFLAG='Y' 
	WHERE RE_SEQ = #{re_seq}
